import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';

const API_USER_INFO_SERVER = 'http://localhost:8080' + '/api/user/info';

const Container = styled.div`
    text-align: center;
    padding: 20px;
`;

const ContractForm = styled.form`
  max-width: 600px;
  margin: 0 auto;
`;

const PartnerInput = styled.input`
  width: 100%;
  padding: 10px;
  font-size: 16px;
  border: 1px solid #ddd;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const FindButton = styled.button`
  background-color: #333;
  color: white;
  padding: 10px 20px;
  font-size: 16px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

const InviteButton = styled.button`
  background-color: #333;
  color: white;
  padding: 10px 20px;
  font-size: 16px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

const ContractConstructPage = () => {
    const history = useHitory();
    const [userToInvite, setUserToInvite] = useState('');
    const [foundUser, setFoundUser] = useState(null);

    const handleSearch = async() => {
        const user = await searchForPartner(userToInvite);

        if (user) {
            setFoundUser({
                'user_id': data.user_id,
                'partner_id': data.partner_id,
                'name': data.name,
                'email': data.email
            });
        } else {
            alert('User not found.');
        }
    };

    const handleInvite = async() => {
        alert(`Invitation sent to ${foundUser.name} (${foundUser.email})`);
    };

    const searchForPartner = async(user_id) => {
        try {
            const response = await fetch(`${API_USER_INFO_SERVER}?user_id=${user_id}`, {
                method: 'GET',
                headers: {
                    'user_id': user_id,
                    'Content_Type': 'application/json'
                }
            });

            if (response.ok) {
                const data = await response.json();
                return {
                    'user_id': data.user_id,
                    'partner_id': data.partner_id,
                    'name': data.name,
                    'email': data.email
                }
            } else {
                console.error('Failed to fetch user information');
            }
        } catch (error) {
            console.error('Error during user information fetch: ', error);
        }
    };

    return (
        <Container>
            <h1>Find Partner</h1>
            <ContractForm onSubmit={handleSearch}>
                {!foundUser ? (
                    <>
                        <PartnerInput
                            type="text"
                            placeholder="Enter User ID to Find and Invite"
                            value={userToInvite}
                            onChange={(e) => setUserToInvite(e.target.value)}
                        />
                        <FindButton type="button" onClick={handleSearch}>
                            Search
                        </FindButton>
                    </>
                ) : (
                    <>
                        <UserDetailsCard>
                            <p>User Found:</p>
                            <UserDetailItem>
                                <Label>ID:</Label>
                                <Value>{foundUser.user_id}</Value>
                            </UserDetailItem>
                            <UserDetailItem>
                                <Label>Name:</Label>
                                <Value>{foundUser.name}</Value>
                            </UserDetailItem>
                            <UserDetailItem>
                                <Label>Email:</Label>
                                <Value>{foundUser.email}</Value>
                            </UserDetailItem>
                        </UserDetailsCard>
                        <InviteButton type="Submit" onSubmit={handleInvite}>
                            Send Invitation
                        </InviteButton>
                    </>
                )}
            </ContractForm>
        </Container>
    );
};

export default ContractConstructPage;
