import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import styled from 'styled-components';

const API_SIGNUP_SERVER = 'http://localhost:8080' + '/api/auth/signup';

const Container = styled.div`
    text-align: center;
    padding: 20px;
`;

const Card = styled.div`
    max-width: 400px;
    margin: 0 auto;
    background-color: #fff;
    border: 1px solid #ddd;
    border-radius: 8px;
    padding: 20px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;
const SignUpForm = styled.form`
    max-width: 400px;
    margin: 0 auto;
`;

const FormGroup = styled.div`
    margin-bottom: 20px;
`;

const Label = styled.label`
    display: block;
    font-weight: bold;
    margin-bottom: 8px;
`;

const Input = styled.input`
    width: 100%;
    padding: 8px;
    font-size: 16px;
    box-sizing: border-box;
`;

const SignUpButton = styled.button`
    background-color: #333;
    color: white;
    padding: 10px 20px;
    font-size: 16px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    width: 100%;
`;

const SwitchAuthMode = styled(Link)`
    color: #333;
    cursor: pointer;
    margin-top: 30px;
    display: inline-block;
`;

const SignUpPage = () => {
    const history = useHistory();

    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: ''
    });

    const handleNameChange = (e) => {
        setFormData({
            ...formData,
            name: e.target.value
        });
    };

    const handleEmailChange = (e) => {
        setFormData({
            ...formData,
            email: e.target.value
        });
    };

    const handlePasswordChange = (e) => {
        setFormData({
            ...formData,
            password: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch(API_SIGNUP_SERVER, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                const data = await response.json();
                const user_id = data.user_id;

                console.log('User signed successfully with ID:', user_id);
                history.push(`/user?user_id=${user_id}`);
                console.log('User registered successfully');
            } else {
                console.error('Failed to register user');
            }
        } catch (error) {
            console.error('Error during registration: ', error);
        }
    };

    return (
        <Container>
            <h1>Sign Up</h1>
            <Card>
                <SignUpForm onSubmit={handleSubmit}>
                    <FormGroup>
                        <Label>Name:</Label>
                        <Input type="text" placeholder="Enter your name" value={formData.name} onChange={handleNameChange} />
                    </FormGroup>
                    <FormGroup>
                        <Label>Email:</Label>
                        <Input type="email" placeholder="Enter your email" value={formData.email} onChange={handleEmailChange} />
                    </FormGroup>
                    <FormGroup>
                        <Label>Password:</Label>
                        <Input type="password" placeholder="Enter your password" value={formData.password} onChange={handlePasswordChange} />
                    </FormGroup>
                    <SignUpButton type="submit">Sign Up</SignUpButton>
                </SignUpForm>
                <SwitchAuthMode to="/signin">Already have an account? Sign In</SwitchAuthMode>
            </Card>
        </Container>
    );
};

export default SignUpPage;
